;}i:67;O:8:"stdClass":6:{s:2:"id";s:4:"1357";s:6:"status";N;s:3:"ctx";s:32:"4a27cf7049f2d11aba9ccdfec14fec96";s:10:"translated";N;s:8:"original";s:190:"This is NOT the preferred way to make your menu appear on mobile devices.  For 99% of users, you will want to set a Toggle Breakpoint in the Toggle Bar Panel, rather than using this setting.";s:15:"gettext_context";s:0:"";}i:68;O:8:"stdClass":6:{s:2:"id";s:4:"1358";s:6:"status";N;s:3:"ctx";s:32:"1acc1ec9ee85d685383a42cdff418c7f";s:10:"translated";N;s:8:"original";s:11:"Button Size";s:15:"gettext_context";s:0:"";}i:69;O:8:"stdClass":6:{s:2:"id";s:4:"1359";s:6:"status";N;s:3:"ctx";s:32:"66200c4b87164fe370fc90d68737a198";s:10:"translated";N;s:8:"original";s:153:"The size of the padding on the links in the menu.  The larger the setting, the easier to click; but fewer menu items will appear on the screen at a time.";s:15:"gettext_context";s:0:"";}i:70;O:8:"stdClass":6:{s:2:"id";s:4:"1360";s:6:"status";N;s:3:"ctx";s:32:"f42c55e683cfe7fb51ed843c7150f872";s:10:"translated";N;s:8:"original";s:9:"Text Size";s:15:"gettext_context";s:0:"";}i:71;O:8:"stdClass":6:{s:2:"id";s:4:"1361";s:6:"status";N;s:3:"ctx";s:32:"785ebb82f7a783a7a99481aa2c365219";s:10:"translated";N;s:8:"original";s:73:"The size of the font on the links in the menu (will override all levels).";s:15:"gettext_context";s:0:"";}i:72;O:8:"stdClass":6:{s:2:"id";s:4:"1362";s:6:"status";N;s:3:"ctx";s:32:"bd145e2d1787cfda93ba26ed7ff7c9c2";s:10:"translated";N;s:8:"original";s:9:"Icon Size";s:15:"gettext_context";s:0:"";}i:73;O:8:"stdClass":6:{s:2:"id";s:4:"1363";s:6:"status";N;s:3:"ctx";s:32:"99ca2d6bf0f78bb342c7eb692a063d7f";s:10:"translated";N;s:8:"original";s:34:"The size of the icons in the menu.";s:15:"gettext_context";s:0:"";}i:74;O:8:"stdClass":6:{s:2:"id";s:4:"1364";s:6:"status";N;s:3:"ctx";s:32:"4755758400dbbce1b00d3ce5f0c45f7d";s:10:"translated";N;s:8:"original";s:36:"Disable Transforms &amp; Transitions";s:15:"gettext_context";s:0:"";}i:75;O:8:"stdClass":6:{s:2: